// Resume Parser Utility - Extracts skills and generates interview questions

export const extractSkillsFromResume = (text) => {
  const skillKeywords = {
    'JavaScript': ['javascript', 'js', 'es6', 'node', 'nodejs'],
    'Python': ['python', 'django', 'flask', 'pandas', 'numpy'],
    'React': ['react', 'jsx', 'redux', 'hooks'],
    'Java': ['java', 'spring', 'junit', 'maven', 'gradle'],
    'SQL': ['sql', 'mysql', 'postgresql', 'oracle', 'database'],
    'MongoDB': ['mongodb', 'nosql', 'mongoose'],
    'Machine Learning': ['machine learning', 'ml', 'tensorflow', 'keras', 'sklearn', 'deep learning', 'neural', 'nlp'],
    'AWS': ['aws', 'amazon', 's3', 'ec2', 'lambda', 'cloud'],
    'Docker': ['docker', 'kubernetes', 'container'],
    'Git': ['git', 'github', 'gitlab', 'version control'],
    'HTML/CSS': ['html', 'css', 'bootstrap', 'tailwind'],
    'API': ['api', 'rest', 'graphql', 'endpoint'],
    'Angular': ['angular', 'typescript'],
    'Vue': ['vue', 'vuex'],
    'Data Science': ['data science', 'analytics', 'visualization', 'tableau', 'power bi'],
    'C++': ['c++', 'cpp', 'oop'],
    'DevOps': ['devops', 'ci/cd', 'jenkins', 'ansible'],
    'Agile': ['agile', 'scrum', 'kanban'],
    'Node.js': ['node', 'nodejs', 'express'],
    'Web Development': ['web development', 'frontend', 'backend', 'fullstack']
  };

  const textLower = text.toLowerCase();
  const foundSkills = new Set();

  for (const [skill, keywords] of Object.entries(skillKeywords)) {
    for (const keyword of keywords) {
      if (textLower.includes(keyword)) {
        foundSkills.add(skill);
        break;
      }
    }
  }

  return Array.from(foundSkills);
};

export const generateInterviewQuestions = (skills) => {
  const basicQuestions = [
    "Tell us about yourself and your professional background.",
    "What are your key strengths as a professional?",
    "Can you describe your most challenging project?",
    "How do you handle tight deadlines?",
    "Why are you interested in this position?",
    "What is your approach to problem-solving?",
    "How do you stay updated with the latest technologies?"
  ];

  const skillSpecificQuestions = {
    'JavaScript': [
      "Explain the concept of closures in JavaScript.",
      "What is the difference between var, let, and const?",
      "How does event bubbling work?",
      "Explain callback functions and promises.",
      "What are arrow functions and how do they differ from regular functions?"
    ],
    'Python': [
      "Explain list comprehensions in Python.",
      "What is the difference between mutable and immutable objects?",
      "How does garbage collection work in Python?",
      "Explain decorators in Python.",
      "What are generators and how do they differ from lists?"
    ],
    'React': [
      "What is the virtual DOM and how does it work?",
      "Explain the difference between controlled and uncontrolled components.",
      "What are React hooks and why are they useful?",
      "Explain the component lifecycle in React.",
      "How does React handle state management?"
    ],
    'Machine Learning': [
      "Explain the difference between supervised and unsupervised learning.",
      "What is overfitting and how do you prevent it?",
      "Describe the steps in a typical ML project workflow.",
      "What are common evaluation metrics for ML models?",
      "Explain the concept of feature engineering."
    ],
    'SQL': [
      "Explain different types of SQL joins.",
      "What is normalization and why is it important?",
      "How do you optimize database queries?",
      "Explain the difference between WHERE and HAVING.",
      "What are indexes and how do they improve performance?"
    ],
    'AWS': [
      "What are the main AWS services you've worked with?",
      "Explain the difference between EC2 and Lambda.",
      "How do you ensure security in AWS?",
      "What is S3 and how is it used?",
      "Explain auto-scaling in AWS."
    ],
    'Docker': [
      "What is Docker and why is containerization important?",
      "Explain the difference between images and containers.",
      "How do you create a Docker image?",
      "What are Docker volumes and networks?",
      "Explain Docker Compose and its use cases."
    ],
    'Data Science': [
      "Walk me through your data analysis process.",
      "How do you handle missing data?",
      "Explain different data visualization techniques.",
      "What statistical concepts are most important in data science?",
      "How do you validate your data analysis results?"
    ],
    'Agile': [
      "Explain Agile methodology and its principles.",
      "What is the difference between Agile and Waterfall?",
      "How do you handle changing requirements in Agile?",
      "Explain sprint planning and retrospectives.",
      "What are your experiences with Scrum ceremonies?"
    ]
  };

  const questions = [...basicQuestions];

  skills.forEach(skill => {
    if (skillSpecificQuestions[skill]) {
      questions.push(...skillSpecificQuestions[skill]);
    }
  });

  return questions;
};

export const parseResumeFile = async (file) => {
  try {
    let text = '';

    if (file.type === 'application/pdf' || file.name.endsWith('.pdf')) {
      // For PDF files, we'll use a simple placeholder
      // In production, you'd use a library like pdf.js or pdfparse
      text = 'PDF parsing would require pdf.js library. For now, extracting from file name.';
    } else if (file.type.includes('word') || file.name.endsWith('.docx') || file.name.endsWith('.doc')) {
      // For DOCX files, we'd need docx library
      text = 'DOCX parsing would require docx library. For now, extracting from file name.';
    } else {
      text = await file.text();
    }

    return text;
  } catch (error) {
    console.error('Error parsing resume:', error);
    return '';
  }
};
