import React, { useState } from 'react';
import './Profile.css';

function Profile() {
  const [edit, setEdit] = useState(false);
  const [form, setForm] = useState(() => {
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    return {
      name: user.name || '',
      lastname: user.lastname || '',
      email: user.email || '',
      phone: user.phone || ''
    };
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSave = (e) => {
    e.preventDefault();
    localStorage.setItem('user', JSON.stringify(form));
    setEdit(false);
    alert('Profile updated!');
  };

  return (
    <div className="profile-page">
      <h2>My Profile</h2>
      <form onSubmit={handleSave} className="profile-form">
        <label>First Name</label>
        <input name="name" value={form.name} onChange={handleChange} disabled={!edit} required />
        <label>Last Name</label>
        <input name="lastname" value={form.lastname} onChange={handleChange} disabled={!edit} required />
        <label>Email</label>
        <input name="email" value={form.email} onChange={handleChange} disabled required />
        <label>Phone</label>
        <input name="phone" value={form.phone} onChange={handleChange} disabled={!edit} />
        {edit ? (
          <button type="submit">Update</button>
        ) : (
          <button type="button" onClick={() => setEdit(true)}>Edit</button>
        )}
      </form>
    </div>
  );
}

export default Profile;
