import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [transcript, setTranscript] = useState('');
  const [analyses, setAnalyses] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/analysis', { transcript });
      setAnalyses([res.data, ...analyses]);
      setTranscript('');
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  useEffect(() => {
    const fetchAnalyses = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/analysis');
        setAnalyses(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchAnalyses();
  }, []);

  return (
    <div className="app">
      <h1>AI Interview Analysis</h1>
      <form onSubmit={handleSubmit} className="form">
        <textarea
          value={transcript}
          onChange={(e) => setTranscript(e.target.value)}
          placeholder="Enter interview transcript here..."
          required
          className="textarea"
        />
        <button type="submit" disabled={loading} className="button">
          {loading ? 'Analyzing...' : 'Analyze'}
        </button>
      </form>
      <div className="results">
        {analyses.map((analysis, index) => (
          <div key={analysis._id || index} className="result">
            <p><strong>Transcript:</strong> {analysis.transcript}</p>
            <p><strong>Sentiment:</strong> {analysis.sentiment}</p>
            <p><strong>Keywords:</strong> {analysis.keywords.join(', ')}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
