import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Dashboard.css';

function Dashboard() {
  const [transcript, setTranscript] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/analysis', { transcript });
      setAnalysis(res.data);
      setTranscript('');
    } catch (err) {
      console.error(err);
    }
    setLoading(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('loggedIn');
    navigate('/');
  };

  return (
    <div className="dashboard">
      <div className="sidebar">
        {/* Placeholder for image */}
        <img src="https://via.placeholder.com/200" alt="Sidebar" />
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </div>
      <div className="main-content">
        <h1>AI Interview Analysis</h1>
        <form onSubmit={handleSubmit} className="form">
          <textarea
            value={transcript}
            onChange={(e) => setTranscript(e.target.value)}
            placeholder="Enter interview transcript here..."
            required
            className="textarea"
          />
          <button type="submit" disabled={loading} className="button">
            {loading ? 'Analyzing...' : 'Analyze'}
          </button>
        </form>
        {analysis && (
          <div className="result">
            <p><strong>Transcript:</strong> {analysis.transcript}</p>
            <p><strong>Sentiment:</strong> {analysis.sentiment}</p>
            <p><strong>Keywords:</strong> {analysis.keywords.join(', ')}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
